                <button onClick={() => setIndividualPrintItem(null)} className="text-rose-500 p-2 hover:bg-rose-50 rounded-full transition transform hover:rotate-90" disabled={isPrinting}><X size={44}/></button>
             </div>
             <div className="bg-white rounded-[2rem] text-right overflow-hidden border border-slate-100 p-2">
                {individualPrintItem.type === 'vouchers' 
                  ? <VouchersPrintGrid payrolls={individualPrintItem.data} />
                  : <DocumentPrintCard title={individualPrintItem.title} type={individualPrintItem.type} data={individualPrintItem.data} />}
             </div>
             <div className="flex gap-6 mt-12 no-print">
                <button onClick={executePrintAction} disabled={isPrinting} className="flex-[2] bg-indigo-600 text-white py-6 rounded-[2.5rem] font-black text-2xl shadow-xl flex items-center justify-center gap-4 hover:bg-indigo-700 transition-all disabled:opacity-50">
                  {isPrinting ? <Loader2 className="animate-spin" size={32}/> : <Printer size={32}/>}
                  {isPrinting ? 'جاري التحضير...' : 'تـنـفـيذ الـطـباعـة'}
                </button>
                <button onClick={() => setIndividualPrintItem(null)} className="flex-1 bg-slate-100 py-6 rounded-[2.5rem] font-black text-xl text-slate-500 hover:bg-slate-200 transition">إغلاق</button>
             </div>
          </div>
        </div>
      )}
      <PrintPortalContent />
    </Layout>
  );
};

export default App;

import { 
  CompanySettings, 
  User,
  Employee,
  AttendanceRecord,
  Loan,
  LeaveRequest,
  FinancialEntry,
  Warning,
  PayrollRecord,
  ProductionEntry
} from '../types.ts';

const STORAGE_KEY = 'SAM_HR_DB_V6_ARCHIVE';

export interface DB {
  settings: CompanySettings;
  users: User[];
  employees: Employee[];
  attendance: AttendanceRecord[];
  loans: Loan[];
  leaves: LeaveRequest[];
  financials: FinancialEntry[];
  production: ProductionEntry[]; 
  warnings: Warning[];
  payrolls: PayrollRecord[];
  payrollHistory: PayrollRecord[];
  departments: string[];
}

const DEFAULT_SETTINGS: CompanySettings = {
  name: 'SAM لادارة شؤون الموظفين',
  address: 'سوريا - دمشق',
  phone: '+963-000-0000',
  logo: '',
  language: 'ar',
  theme: 'light',
  currency: 'SYP',
  availableCurrencies: ['SYP', 'SAR', 'USD', 'EGP', 'AED'],
  gracePeriodMinutes: 15,
  officialCheckIn: '08:00',
  officialCheckOut: '16:00',
  deductionPerLateMinute: 1.0,
  overtimeHourRate: 1.5,
  salaryCycle: 'monthly',
  passwordHint: 'رقم هاتفك أو اسمك المفضل',
  archiveRetentionDays: 90,
  archiveLogs: []
};

const INITIAL_USER: User = {
  id: 'admin-sam',
  username: 'admin',
  password: '123',
  role: 'admin',
  name: 'المدير العام',
  permissions: ['all']
};

export const loadDB = (): DB => {
  const data = localStorage.getItem(STORAGE_KEY);
  if (!data) {
    const initialDB: DB = {
      settings: DEFAULT_SETTINGS,
      users: [INITIAL_USER],
      employees: [],
      attendance: [],
      loans: [],
      leaves: [],
      financials: [],
      production: [],
      warnings: [],
      payrolls: [],
      payrollHistory: [],
      departments: ['الإدارة العامة', 'المحاسبة', 'الموارد البشرية', 'الإنتاج', 'المبيعات']
    };
    saveDB(initialDB);
    return initialDB;
  }

  try {
    const parsed = JSON.parse(data);

    return {
      settings: { ...DEFAULT_SETTINGS, ...parsed.settings },
      users: parsed.users || [INITIAL_USER],
      employees: parsed.employees || [],
      attendance: parsed.attendance || [],
      loans: parsed.loans || [],
      leaves: parsed.leaves || [],
      financials: parsed.financials || [],
      production: parsed.production || [],
      warnings: parsed.warnings || [],
      payrolls: parsed.payrolls || [],
      payrollHistory: parsed.payrollHistory || [],
      departments: parsed.departments || ['الإدارة العامة', 'المحاسبة', 'الموارد البشرية', 'الإنتاج', 'المبيعات']
    };
  } catch (e) {
    console.error("Failed to parse DB", e);
    return {
      settings: DEFAULT_SETTINGS,
      users: [INITIAL_USER],
      employees: [],
      attendance: [],
      loans: [],
      leaves: [],
      financials: [],
      production: [],
      warnings: [],
      payrolls: [],
      payrollHistory: [],
      departments: ['الإدارة العامة', 'المحاسبة', 'الموارد البشرية', 'الإنتاج', 'المبيعات']
    };
  }
};

export const saveDB = (db: DB) => {
  localStorage.setItem(STORAGE_KEY, JSON.stringify(db));
  if (db.settings.theme === 'dark') {
    document.documentElement.classList.add('dark');
  } else {
    document.documentElement.classList.remove('dark');
  }
};

