const CACHE_NAME = 'sam-hr-pro-v10';

// قائمة الملفات والروابط التي يجب حفظها للعمل أوفلاين
const ASSETS_TO_CACHE = [
  './',
  './index.html',
  './index.tsx',
  './App.tsx',
  './manifest.json',
  './metadata.json',
  './types.ts',
  './db/store.ts',
  './utils/calculations.ts',
  './utils/translations.ts',
  './utils/export.ts',
  './components/Layout.tsx',
  './views/Dashboard.tsx',
  './views/Employees.tsx',
  './views/Departments.tsx',
  './views/Attendance.tsx',
  './views/GenericModule.tsx',
  './views/Settings.tsx',
  './views/Reports.tsx',
  './views/Production.tsx',
  './views/PrintForms.tsx',
  // المكتبات الخارجية والخطوط
  'https://cdn.tailwindcss.com',
  'https://fonts.googleapis.com/css2?family=Cairo:wght@400;600;700;900&display=swap',
  'https://esm.sh/react@19.0.0',
  'https://esm.sh/react-dom@19.0.0',
  'https://esm.sh/react-dom@19.0.0/client',
  'https://esm.sh/lucide-react@0.460.0',
  'https://esm.sh/xlsx@0.18.5'
];

// مرحلة التثبيت: حفظ الملفات في الكاش
self.addEventListener('install', (event) => {
  event.waitUntil(
    caches.open(CACHE_NAME).then((cache) => {
      console.log('SAM:Caching all assets for offline use...');
      return cache.addAll(ASSETS_TO_CACHE);
    })
  );
  self.skipWaiting();
});

// مرحلة التفعيل: حذف الكاش القديم
self.addEventListener('activate', (event) => {
  event.waitUntil(
    caches.keys().then((cacheNames) => {
      return Promise.all(
        cacheNames.filter((name) => name !== CACHE_NAME).map((name) => caches.delete(name))
      );
    })
  );
  self.clients.claim();
});

// استراتيجية جلب البيانات: الكاش أولاً للمكتبات والملفات الثابتة
self.addEventListener('fetch', (event) => {
  if (event.request.method !== 'GET') return;

  event.respondWith(
    caches.match(event.request).then((cachedResponse) => {
      // إذا وجدنا الملف في الكاش، نعيده فوراً
      if (cachedResponse) {
        return cachedResponse;
      }

      // إذا لم يوجد، نحاول جلبه من الشبكة وحفظه للمرة القادمة
      return fetch(event.request).then((networkResponse) => {
        if (!networkResponse || networkResponse.status !== 200 || networkResponse.type !== 'basic') {
          // للمصادر الخارجية مثل Google Fonts و ESM.sh
          if (networkResponse && (networkResponse.status === 200 || networkResponse.status === 0)) {
             const responseToCache = networkResponse.clone();
             caches.open(CACHE_NAME).then((cache) => {
               cache.put(event.request, responseToCache);
             });
          }
          return networkResponse;
        }

        const responseToCache = networkResponse.clone();
        caches.open(CACHE_NAME).then((cache) => {
          cache.put(event.request, responseToCache);
        });

        return networkResponse;
      }).catch(() => {
        // في حال انقطاع الإنترنت تماماً وعدم وجود الملف في الكاش
        console.warn('SAM: Resource not found in cache and network is down.');
      });
    })
  );
});

