const CACHE_NAME = 'sam-hr-pro-v11';

// قائمة الملفات والروابط التي يجب حفظها للعمل أوفلاين
const ASSETS_TO_CACHE = [
  './',
  './index.html',
  './index.tsx',
  './App.tsx',
  './manifest.json',
  './metadata.json',
  './types.ts',
  './db/store.ts',
  './utils/calculations.ts',
  './utils/translations.ts',
  './utils/export.ts',
  './components/Layout.tsx',
  './views/Dashboard.tsx',
  './views/Employees.tsx',
  './views/Departments.tsx',
  './views/Attendance.tsx',
  './views/GenericModule.tsx',
  './views/Settings.tsx',
  './views/Reports.tsx',
  './views/Production.tsx',
  './views/PrintForms.tsx',
  // المكتبات الخارجية والخطوط
  'https://cdn.tailwindcss.com',
  'https://fonts.googleapis.com/css2?family=Cairo:wght@400;600;700;900&display=swap',
  'https://esm.sh/react@19.0.0',
  'https://esm.sh/react-dom@19.0.0',
  'https://esm.sh/react-dom@19.0.0/client',
  'https://esm.sh/lucide-react@0.460.0',
  'https://esm.sh/xlsx@0.18.5'
];

// مرحلة التثبيت: حفظ الملفات في الكاش
self.addEventListener('install', (event) => {
  event.waitUntil(
    caches.open(CACHE_NAME).then((cache) => {
      console.log('SAM: Pre-caching assets...');
      // استخدام force fetch لضمان تحديث الملفات عند التثبيت
      return Promise.all(
        ASSETS_TO_CACHE.map(url => {
          return fetch(url, { cache: 'no-cache' }).then(response => {
            if (response.ok) return cache.put(url, response);
            return Promise.reject(`Failed to fetch ${url}`);
          }).catch(err => console.warn('Cache error:', err));
        })
      );
    })
  );
  self.skipWaiting();
});

// مرحلة التفعيل: تنظيف الإصدارات القديمة
self.addEventListener('activate', (event) => {
  event.waitUntil(
    caches.keys().then((cacheNames) => {
      return Promise.all(
        cacheNames.filter((name) => name !== CACHE_NAME).map((name) => caches.delete(name))
      );
    })
  );
  self.clients.claim();
});

// استراتيجية الجلب: Network falling back to Cache (لضمان أحدث نسخة عند وجود إنترنت)
self.addEventListener('fetch', (event) => {
  if (event.request.method !== 'GET') return;

  event.respondWith(
    fetch(event.request)
      .then((networkResponse) => {
        // تحديث الكاش بالنسخة الجديدة المستلمة من الشبكة
        const responseToCache = networkResponse.clone();
        caches.open(CACHE_NAME).then((cache) => {
          cache.put(event.request, responseToCache);
        });
        return networkResponse;
      })
      .catch(() => {
        // في حال فشل الشبكة (أوفلاين)، نبحث في الكاش
        return caches.match(event.request).then((cachedResponse) => {
          if (cachedResponse) return cachedResponse;

          // إذا لم يوجد في الكاش وكان الطلب لصفحة HTML، نعيد الصفحة الرئيسية
          if (event.request.mode === 'navigate') {
            return caches.match('./index.html');
          }
        });
      })
  );
});

