const CACHE_NAME = 'sam-hr-pro-v13';

// قائمة الملفات والروابط الأساسية
const ASSETS_TO_CACHE = [
  './',
  './index.html',
  './index.tsx',
  './App.tsx',
  './manifest.json',
  './metadata.json',
  './types.ts',
