                </div>
                <div>
                  <label className="text-[10px] font-black text-slate-400 uppercase mb-1 block">Password</label>
                  <input type="password" className="w-full p-4 bg-slate-50 dark:bg-slate-800 border-2 border-transparent focus:border-indigo-600 rounded-2xl font-bold outline-none transition" value={loginForm.password} onChange={e => setLoginForm({...loginForm, password: e.target.value})} required />
                </div>
                <button type="submit" className="w-full bg-indigo-600 text-white py-5 rounded-2xl font-black text-lg shadow-xl hover:bg-indigo-700 transition">Login to System</button>
