        </div>
      );
    }

    const commonProps = {
      employees: db.employees,
      settings: db.settings,
      lang: db.settings.language,
    };

    switch (activeTab) {
      case 'dashboard':
        return <Dashboard 
          employeesCount={db.employees.length}
          todayAttendance={db.attendance.filter(a => a.date === new Date().toISOString().split('T')[0]).length}
          totalLoans={db.loans.filter(l => l.remainingAmount > 0).reduce((acc, l) => acc + l.remainingAmount, 0)}
          totalSalaryBudget={db.employees.reduce((acc, e) => acc + e.baseSalary + e.transportAllowance, 0)}
        />;
      case 'employees':
        return <Employees 
          {...commonProps}
          departments={db.departments}
          onAdd={(e) => setDb({ ...db, employees: db.employees.find(emp => emp.id === e.id) ? db.employees.map(emp => emp.id === e.id ? e : emp) : [...db.employees, e] })}
          onDelete={(id) => setDb({ ...db, employees: db.employees.filter(e => e.id !== id) })}
        />;
      case 'departments':
        return <Departments 
          departments={db.departments}
          employees={db.employees}
          onUpdate={(depts) => setDb({ ...db, departments: depts })}
          onUpdateEmployee={(emp) => setDb({ ...db, employees: db.employees.map(e => e.id === emp.id ? emp : e) })}
        />;
      case 'attendance':
        return <Attendance 
          {...commonProps}
          records={db.attendance}
          onSaveRecord={(r) => setDb({ ...db, attendance: db.attendance.find(rec => rec.id === r.id) ? db.attendance.map(rec => rec.id === r.id ? r : rec) : [...db.attendance, r] })}
          onDeleteRecord={(id) => setDb({ ...db, attendance: db.attendance.filter(r => r.id !== id) })}
          onPrint={() => window.print()}
        />;
      case 'production':
        return <Production 
          {...commonProps}
          items={db.production}
          archiveMode={archiveModes.production}
          onToggleArchive={() => setArchiveModes({ ...archiveModes, production: !archiveModes.production })}
          onSave={(item) => setDb({ ...db, production: db.production.find(i => i.id === item.id) ? db.production.map(i => i.id === item.id ? item : i) : [...db.production, item] })}
          onDelete={(id) => setDb({ ...db, production: db.production.filter(i => i.id !== id) })}
        />;
      case 'settings':
        return <SettingsView 
          settings={db.settings}
          admin={db.users.find(u => u.role === 'admin')!}
          db={db}
          onUpdateSettings={updateSettings}
          onUpdateAdmin={updateAdmin}
          onImport={(newDb) => setDb(newDb)}
          onRunArchive={runArchive}
          onClearData={handleClearData}
        />;
      case 'reports':
        return <ReportsView db={db} payrolls={[]} lang={db.settings.language} onPrint={() => window.print()} />;
      case 'documents':
        return <PrintForms 
           employees={db.employees}
           attendance={db.attendance}
           financials={db.financials}
           warnings={db.warnings}
           leaves={db.leaves}
           settings={db.settings}
           onPrint={(item: any) => setIndividualPrintItem(item)}
        />;
      default:
        return <div className="p-20 text-center text-slate-400 font-black">قريباً: {activeTab}</div>;
    }
  };

  return (
    <>
      <Layout 
        activeTab={activeTab} 
        setActiveTab={setActiveTab} 
        lang={db.settings.language} 
        theme={db.settings.theme}
        toggleTheme={() => updateSettings({ theme: db.settings.theme === 'light' ? 'dark' : 'light' })}
        currentUser={currentUser}
        onLogout={onLogout}
      >
        {renderContent()}
      </Layout>

      {individualPrintItem && createPortal(
        <div className="fixed inset-0 bg-white z-[9999] p-10 overflow-auto printing-page">
           <div className="flex justify-between items-start border-b-4 border-slate-900 pb-6 mb-8">
              <div className="text-right">
                 <h1 className="text-2xl font-black">{db.settings.name}</h1>
                 <p className="text-xs font-bold text-slate-500 mt-1">{db.settings.address}</p>
                 <p className="text-xs font-bold text-slate-500">{db.settings.phone}</p>
              </div>
              {db.settings.logo && <img src={db.settings.logo} className="h-16 w-auto object-contain" />}
           </div>

           <div className="text-center my-10 space-y-2">
              <h2 className="text-3xl font-black underline uppercase tracking-tighter">{individualPrintItem.title}</h2>
              <p className="text-sm font-bold text-slate-400 italic">مستند رسمي صادر عن نظام SAM HRMS</p>
           </div>

           <div className="grid grid-cols-2 gap-10 my-10 border-2 p-8 rounded-3xl border-slate-100">
              <div className="space-y-4">
                 <p className="font-bold text-slate-500">اسم الموظف: <span className="text-slate-900 font-black text-lg">{individualPrintItem.data.employeeName}</span></p>
                 <p className="font-bold text-slate-500">الرقم الوظيفي: <span className="text-slate-900 font-black">{individualPrintItem.data.employeeId}</span></p>
              </div>
              <div className="text-left space-y-4">
                 <p className="font-bold text-slate-500">تاريخ الإصدار: <span className="text-slate-900 font-black">{new Date().toLocaleDateString('ar-EG')}</span></p>
                 <p className="font-bold text-slate-500">رقم المرجع: <span className="text-slate-900 font-black">SAM-{Math.floor(1000 + Math.random() * 9000)}</span></p>
              </div>
           </div>

           <div className="p-10 bg-slate-50 rounded-[2.5rem] min-h-[300px] text-right text-lg leading-loose font-bold text-slate-700">
              {individualPrintItem.data.notes || 'لا توجد ملاحظات إضافية مسجلة لهذا المستند.'}
              {individualPrintItem.type === 'report_attendance' && (
                <div className="mt-6 overflow-hidden border rounded-xl">
                   <table className="w-full text-sm">
                      <thead className="bg-slate-200">
                         <tr>
                            <th className="p-2">التاريخ</th>
