export interface User {
  id: string;
  username: string;
  password?: string;
  role: UserRole;
  name: string;
  permissions: string[];
}

export interface ArchiveLog {
  id: string;
  date: string;
  type: string;
  recordsCount: number;
  performedBy: string;
}

export interface CompanySettings {
  name: string;
  address: string;
  phone: string;
  logo: string;
  language: Language;
  theme: Theme;
  currency: string;
  availableCurrencies: string[];
  gracePeriodMinutes: number;
