  status: 'present' | 'absent' | 'leave' | 'excused';
  isArchived?: boolean;
}

export interface Loan {
  id: string;
  employeeId: string;
  amount: number;
  installmentsCount: number;
  monthlyInstallment: number;
  date: string;
