  type: 'bonus' | 'deduction' | 'production_incentive' | 'payment';
  amount: number;
  date: string;
  reason: string;
  isArchived?: boolean;
}

export interface Warning {
  id: string;
  employeeId: string;
  type: 'verbal' | 'written' | 'final';
  date: string;
