  production: number;
  overtimePay: number;
  overtimeMinutes: number;
  loanInstallment: number;
  lateDeduction: number; 
  earlyDepartureMinutes: number;
  earlyDepartureDeduction: number;
  absenceDays: number;
  absenceDeduction: number;
  manualDeductions: number; 
