      const updatedDepts = departments.filter(d => d !== dept);
      onUpdate(updatedDepts);
    }
  };

  const changeEmpDept = (emp: Employee, newD: string) => {
    onUpdateEmployee({ ...emp, department: newD });
  };

  const handleExportAllDepartments = () => {
