                  {individualPrintItem.type === 'vouchers' 
                    ? <VouchersPrintGrid payrolls={individualPrintItem.data} />
                    : <DocumentPrintCard title={individualPrintItem.title} type={individualPrintItem.type} data={individualPrintItem.data} />}
               </div>
               <div className="flex gap-6 mt-12 no-print">
                  <button onClick={executePrintAction} disabled={isPrinting} className="flex-[2] bg-indigo-600 text-white py-6 rounded-[2.5rem] font-black text-2xl shadow-xl flex items-center justify-center gap-4 hover:bg-indigo-700 transition-all disabled:opacity-50">
                    {isPrinting ? <Loader2 className="animate-spin" size={32}/> : <Printer size={32}/>}
                    {isPrinting ? 'جاري التحضير...' : 'تـنـفـيذ الـطـباعـة'}
                  </button>
                  <button onClick={() => setIndividualPrintItem(null)} className="flex-1 bg-slate-100 py-6 rounded-[2.5rem] font-black text-xl text-slate-500 hover:bg-slate-200 transition">إغلاق</button>
               </div>
            </div>
          </div>
        )}
        <PrintPortalContent />
      </Layout>
    </div>
  );
};

export default App;

import * as XLSX from 'xlsx';

export const exportToExcel = (data: any[], fileName: string) => {
  if (!data || data.length === 0) return;

  // إنشاء ورقة عمل من البيانات
  // البيانات يجب أن تكون كائنات بمفاتيح عربية بالفعل من المصدر
  const ws = XLSX.utils.json_to_sheet(data);

  // تفعيل اتجاه من اليمين لليسار (مهم جداً لعرض العربية بشكل صحيح في Excel)
  if(!ws['!props']) ws['!props'] = {};
  ws['!rtl'] = true;

  // ضبط عرض الأعمدة تلقائيًا لتناسب المحتوى العربي
  const headers = Object.keys(data[0]);
  ws['!cols'] = headers.map(header => ({
    wch: Math.max(
      header.length + 5,
      ...data.map(row => String(row[header] ?? '').length + 5)
    )
  }));

  // إنشاء كتاب العمل
  const wb = XLSX.utils.book_new();
  XLSX.utils.book_append_sheet(wb, ws, 'البيانات');

  // اسم الملف مع التاريخ الحالي
  const date = new Date().toISOString().split('T')[0];
  XLSX.writeFile(wb, `${fileName}_${date}.xlsx`, { bookType: 'xlsx', type: 'binary' });
};

import React, { useState, useMemo } from 'react';
import { Employee, AttendanceRecord, CompanySettings } from '../types';
import { Clock, Trash2, Edit2, FileDown, Search, Calendar, Printer, CheckCircle, AlertCircle, Archive, Filter, X } from 'lucide-react';
import { calculateTimeDiffMinutes } from '../utils/calculations';
import { exportToExcel } from '../utils/export';

interface Props {
  employees: Employee[];
  records: AttendanceRecord[];
  settings: CompanySettings;
  onSaveRecord: (record: AttendanceRecord) => void;
  onDeleteRecord: (id: string) => void;
  lang: 'ar' | 'en';
  onPrint: () => void;
}

const Attendance: React.FC<Props> = ({ employees, records, settings, onSaveRecord, onDeleteRecord, lang, onPrint }) => {
  const [editingId, setEditingId] = useState<string | null>(null);
  const [selectedEmp, setSelectedEmp] = useState('');
  const [checkIn, setCheckIn] = useState(settings.officialCheckIn);
  const [checkOut, setCheckOut] = useState(settings.officialCheckOut);
  const [date, setDate] = useState(new Date().toISOString().split('T')[0]);

  const [showArchive, setShowArchive] = useState(false);
  const [archiveSearch, setArchiveSearch] = useState('');
  const [archiveEmpId, setArchiveEmpId] = useState('');
  const [dateFrom, setDateFrom] = useState(new Date(new Date().getFullYear(), new Date().getMonth(), 1).toISOString().split('T')[0]);
  const [dateTo, setDateTo] = useState(new Date().toISOString().split('T')[0]);

  const isRtl = lang === 'ar';

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!selectedEmp) return;
    const emp = employees.find(e => e.id === selectedEmp);
    const shiftIn = emp?.customCheckIn || settings.officialCheckIn;
    const shiftOut = emp?.customCheckOut || settings.officialCheckOut;
    const lateMinutes = Math.max(0, calculateTimeDiffMinutes(checkIn, shiftIn));
    const overtimeMinutes = Math.max(0, calculateTimeDiffMinutes(checkOut, shiftOut));

    onSaveRecord({
      id: editingId || Math.random().toString(36).substr(2, 9),
      employeeId: selectedEmp,
      date, checkIn, checkOut, lateMinutes, overtimeMinutes, status: 'present'
    });

    setEditingId(null); 
    setSelectedEmp('');
  };

  const handleEdit = (r: AttendanceRecord) => {
    setEditingId(r.id);
    setSelectedEmp(r.employeeId);
    setCheckIn(r.checkIn);
    setCheckOut(r.checkOut);
    setDate(r.date);
    if (showArchive) {
        setShowArchive(false);
    }
  };

  const filteredRecords = useMemo(() => {
    return records.filter(r => {
      const emp = employees.find(e => e.id === r.employeeId);
      return (emp?.name.toLowerCase().includes(archiveSearch.toLowerCase()) || !archiveSearch) && r.date === date && !r.isArchived;
    });
  }, [records, date, archiveSearch, employees]);

  const archivedRecords = useMemo(() => {
    return records.filter(r => {
      const emp = employees.find(e => e.id === r.employeeId);
      const nameMatch = emp?.name.toLowerCase().includes(archiveSearch.toLowerCase());
      const empMatch = !archiveEmpId || r.employeeId === archiveEmpId;
      const dateMatch = (!dateFrom || r.date >= dateFrom) && (!dateTo || r.date <= dateTo);
      return nameMatch && empMatch && dateMatch;
    }).sort((a,b) => b.date.localeCompare(a.date));
  }, [records, archiveSearch, archiveEmpId, dateFrom, dateTo, employees]);

  const formatHours = (mins: number) => {
    const h = Math.floor(mins / 60);
    const m = mins % 60;
    return `${h} س ${m} د`;
  };

  const handleExportExcel = () => {
    const dataToExport = archivedRecords.map(r => {
      const emp = employees.find(e => e.id === r.employeeId);
      return {
        'التاريخ': r.date,
        'اسم الموظف': emp?.name || 'غير معروف',
        'وقت الحضور': r.checkIn,
        'وقت الانصراف': r.checkOut,
        'دقائق التأخير': r.lateMinutes,
        'دقائق الإضافي': r.overtimeMinutes,
        'ساعات العمل': formatHours(calculateTimeDiffMinutes(r.checkOut, r.checkIn))
      };
    });
    exportToExcel(dataToExport, "Attendance_Report");
  };

  return (
    <div className="space-y-8">
      <div className="hidden print:flex justify-between items-start border-b-4 border-indigo-900 pb-6 mb-8 w-full text-indigo-950">
        <div className="text-right">
          <h1 className="text-3xl font-black leading-none">{settings.name}</h1>
          <p className="text-sm font-black text-indigo-700 mt-2">تقرير سجلات الحضور والانصراف</p>
          <p className="text-[10px] font-bold mt-1 text-slate-600 uppercase">الفترة: {showArchive ? `${dateFrom} إلى ${dateTo}` : `يوم ${date}`}</p>
        </div>
        <div className="flex flex-col items-center">
          {settings.logo && <img src={settings.logo} className="h-14 w-auto object-contain mb-2" alt="Logo" />}
        </div>
        <div className="text-left text-[10px] font-black text-slate-400">
          <p>تاريخ الطباعة: {new Date().toLocaleDateString('ar-EG')}</p>
          <p>الوقت: {new Date().toLocaleTimeString('ar-EG')}</p>
        </div>
      </div>

      <div className="flex justify-between items-center bg-white dark:bg-slate-900 p-8 rounded-[2.5rem] shadow-xl border dark:border-slate-800 no-print">
         <h2 className="text-2xl font-black text-indigo-700">{showArchive ? 'سجل الحضور التاريخي' : 'إدارة الحضور اليومي والماضي'}</h2>
         <button onClick={() => setShowArchive(!showArchive)} className={`flex items-center gap-2 px-8 py-3 rounded-2xl font-black transition-all ${showArchive ? 'bg-indigo-100 text-indigo-700' : 'bg-slate-900 text-white'}`}>
           {showArchive ? <Calendar size={20}/> : <Archive size={20}/>} {showArchive ? 'العودة للتسجيل' : 'عرض الأرشيف المتقدم'}
         </button>
      </div>

      {!showArchive ? (
        <div className="grid grid-cols-1 lg:grid-cols-4 gap-8 animate-in fade-in duration-500">
          <div className="lg:col-span-1 bg-white dark:bg-slate-900 p-8 rounded-[2.5rem] border dark:border-slate-800 shadow-2xl h-fit no-print">
            <h3 className="text-xl font-black mb-8 flex items-center gap-2 text-indigo-700 text-right"><Clock size={24} /> {editingId ? 'تعديل السجل' : 'تسجيل حضور'}</h3>
            <form onSubmit={handleSubmit} className="space-y-6 text-right">
              <div>
                <label className="text-[10px] font-black uppercase mb-1 block">تاريخ الدوام (يمكنك اختيار تاريخ سابق)</label>
                <input type="date" className="w-full p-4 border dark:bg-slate-800 rounded-xl font-bold bg-indigo-50/50" value={date} onChange={e => setDate(e.target.value)} />
              </div>
              <div>
                <label className="text-[10px] font-black uppercase mb-1 block">الموظف</label>
                <select className="w-full p-4 border dark:bg-slate-800 rounded-xl font-bold" value={selectedEmp} onChange={e => setSelectedEmp(e.target.value)} required>
                  <option value="">-- اختر الموظف --</option>
                  {employees.map(e => <option key={e.id} value={e.id}>{e.name}</option>)}
                </select>
              </div>
              <div className="grid grid-cols-2 gap-3">
                 <div><label className="text-[10px] font-black block">وقت الدخول</label><input type="time" className="w-full p-3 border dark:bg-slate-800 rounded-xl font-bold" value={checkIn} onChange={e => setCheckIn(e.target.value)} /></div>
                 <div><label className="text-[10px] font-black block">وقت الانصراف</label><input type="time" className="w-full p-3 border dark:bg-slate-800 rounded-xl font-bold" value={checkOut} onChange={e => setCheckOut(e.target.value)} /></div>
              </div>
              <div className="flex gap-2 pt-4">
                <button className="flex-1 bg-indigo-600 text-white py-4 rounded-xl font-black shadow-lg hover:bg-indigo-700 transition">
                   {editingId ? 'تحديث البيانات' : 'تأكيد الحضور'}
                </button>
                {editingId && <button type="button" onClick={() => {setEditingId(null); setSelectedEmp('');}} className="p-4 bg-slate-100 dark:bg-slate-800 rounded-xl"><X size={20}/></button>}
              </div>
            </form>
          </div>

          <div className="lg:col-span-3 space-y-6">
            <div className="bg-white dark:bg-slate-900 rounded-[2.5rem] border dark:border-slate-800 shadow-2xl overflow-hidden">
              <div className="p-6 border-b bg-slate-50/50 flex justify-between items-center no-print">
                 <h4 className="font-black text-slate-700">سجلات يوم: <span className="text-indigo-600">{date}</span></h4>
                 <div className="relative w-48">
                   <Search className="absolute right-2 top-2 text-slate-400" size={16}/>
                   <input type="text" placeholder="بحث سريع..." className="w-full pr-8 p-1.5 text-xs border rounded-lg outline-none focus:border-indigo-400" value={archiveSearch} onChange={e => setArchiveSearch(e.target.value)}/>
                 </div>
              </div>
              <div className="overflow-x-auto">
                <table className="w-full text-right">
                  <thead className="bg-slate-50 dark:bg-slate-800 border-b">
                    <tr className="text-slate-500 font-black text-xs uppercase">
                      <th className="px-8 py-6">الموظف</th>
                      <th className="text-center py-6">الوقت المسجل</th>
                      <th className="text-center py-6">ساعات العمل</th>
                      <th className="text-center py-6">الحالة</th>
                      <th className="text-center py-6 no-print">إجراءات</th>
                    </tr>
                  </thead>
                  <tbody className="divide-y divide-slate-100 dark:divide-slate-800">
                    {filteredRecords.map(r => {
                      const emp = employees.find(e => e.id === r.employeeId);
                      const actualMins = calculateTimeDiffMinutes(r.checkOut, r.checkIn);

                      return (
                        <tr key={r.id} className="hover:bg-slate-50 dark:hover:bg-slate-800/40 transition">
                          <td className="px-8 py-5">
                            <p className="font-black text-slate-900 dark:text-white text-lg">{emp?.name}</p>
                            <p className="text-[10px] text-slate-500 font-bold uppercase">{r.date}</p>
                          </td>
                          <td className="text-center font-bold">
                            <span className="bg-indigo-50 dark:bg-indigo-900/20 px-4 py-2 rounded-xl text-indigo-700 dark:text-indigo-400">{r.checkIn} - {r.checkOut}</span>
                          </td>
                          <td className="text-center">
                            <span className="text-indigo-600 font-black pt-1">{formatHours(actualMins)}</span>
                          </td>
                          <td className="text-center">
                            <div className="flex flex-col items-center gap-1">
                              {r.lateMinutes > 0 ? (
                                <span className="flex items-center gap-1 text-rose-600 font-black text-xs"><AlertCircle size={14}/> متأخر {r.lateMinutes} د</span>
                              ) : (
                                <span className="flex items-center gap-1 text-emerald-600 font-black text-xs"><CheckCircle size={14}/> في الموعد</span>
                              )}
                            </div>
                          </td>
                          <td className="text-center no-print">
                            <div className="flex justify-center gap-1">
                              <button onClick={() => handleEdit(r)} title="تعديل السجل" className="p-2 text-indigo-600 rounded-lg hover:bg-indigo-50"><Edit2 size={16}/></button>
                              <button onClick={() => onDeleteRecord(r.id)} title="حذف السجل" className="p-2 text-rose-600 rounded-lg hover:bg-rose-50"><Trash2 size={16}/></button>
                            </div>
                          </td>
                        </tr>
                      );
                    })}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      ) : (
        <div className="space-y-6 animate-in slide-in-from-bottom-8 duration-500">
           <div className="bg-white dark:bg-slate-900 p-8 rounded-[2.5rem] shadow-xl border dark:border-slate-800 grid grid-cols-1 md:grid-cols-4 gap-6 no-print text-right">
              <div className="md:col-span-1">
                <label className="text-xs font-black mb-1 block">تصفية حسب الموظف</label>
                <select className="w-full p-3 bg-slate-50 dark:bg-slate-800 border rounded-xl font-bold" value={archiveEmpId} onChange={e => setArchiveEmpId(e.target.value)}>
                   <option value="">كل الموظفين</option>
                   {employees.map(e => <option key={e.id} value={e.id}>{e.name}</option>)}
                </select>
              </div>
              <div className="md:col-span-1">
                <label className="text-xs font-black mb-1 block">من تاريخ</label>
                <input type="date" className="w-full p-3 bg-slate-50 dark:bg-slate-800 border rounded-xl font-bold" value={dateFrom} onChange={e => setDateFrom(e.target.value)} />
              </div>
              <div className="md:col-span-1">
                <label className="text-xs font-black mb-1 block">إلى تاريخ</label>
                <input type="date" className="w-full p-3 bg-slate-50 dark:bg-slate-800 border rounded-xl font-bold" value={dateTo} onChange={e => setDateTo(e.target.value)} />
              </div>
              <div className="flex items-end gap-2">
                 <button onClick={handleExportExcel} className="flex-1 bg-emerald-600 text-white p-3 rounded-xl flex items-center justify-center gap-2 font-black shadow-lg"><FileDown size={18}/> Excel</button>
                 <button onClick={() => window.print()} className="flex-1 bg-slate-900 text-white p-3 rounded-xl flex items-center justify-center gap-2 font-black shadow-lg"><Printer size={18}/> طباعة</button>
              </div>
           </div>

           <div className="bg-white dark:bg-slate-900 rounded-[2.5rem] border dark:border-slate-800 shadow-2xl overflow-hidden">
             <div className="overflow-x-auto">
               <table className="w-full text-right text-sm">
                  <thead className="bg-slate-100 dark:bg-slate-900 border-b">
                    <tr className="text-slate-500 font-black text-xs uppercase">
                      <th className="px-8 py-4">التاريخ</th>
                      <th className="px-8 py-4">الموظف</th>
                      <th className="text-center py-4">الحضور</th>
                      <th className="text-center py-4">الانصراف</th>
                      <th className="text-center py-4">الساعات الفعلية</th>
                      <th className="text-center py-4">تأخير (د)</th>
                      <th className="text-center py-4 no-print">إجراءات</th>
                    </tr>
                  </thead>
                  <tbody className="divide-y divide-slate-100 dark:divide-slate-800">
                    {archivedRecords.map(r => (
                      <tr key={r.id} className="hover:bg-slate-50 transition font-bold">
                        <td className="px-8 py-4 text-slate-500">{r.date}</td>
                        <td className="px-8 py-4">{employees.find(e => e.id === r.employeeId)?.name}</td>
                        <td className="text-center">{r.checkIn}</td>
                        <td className="text-center">{r.checkOut}</td>
                        <td className="text-center font-black text-indigo-700">{formatHours(calculateTimeDiffMinutes(r.checkOut, r.checkIn))}</td>
                        <td className={`text-center ${r.lateMinutes > 0 ? 'text-rose-600 font-black' : ''}`}>{r.lateMinutes}</td>
                        <td className="text-center no-print">
                           <div className="flex justify-center gap-2">
                             <button onClick={() => handleEdit(r)} title="تعديل السجل" className="p-2 text-indigo-600 rounded-lg hover:bg-indigo-50"><Edit2 size={16}/></button>
                             <button onClick={() => onDeleteRecord(r.id)} title="حذف نهائي" className="p-2 text-rose-600 rounded-lg hover:bg-rose-50"><Trash2 size={16}/></button>
                           </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
               </table>
             </div>
           </div>
        </div>
      )}
    </div>
  );
};

export default Attendance;

import React, { useState, useMemo } from 'react';
import { Employee, ProductionEntry, CompanySettings } from '../types';
import { Zap, Plus, Trash2, Edit2, Search, FileDown, Printer, Calendar, Package, Archive, History, X } from 'lucide-react';
import { exportToExcel } from '../utils/export';

interface Props {
  employees: Employee[];
  items: ProductionEntry[];
  settings: CompanySettings;
  onSave: (item: ProductionEntry) => void;
  onDelete: (id: string) => void;
  onPrintIndividual?: (item: ProductionEntry) => void;
  archiveMode: boolean;
  onToggleArchive: () => void;
}

const Production: React.FC<Props> = ({ employees, items, settings, onSave, onDelete, onPrintIndividual, archiveMode, onToggleArchive }) => {
  const [showModal, setShowModal] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [dateFrom, setDateFrom] = useState('');
  const [dateTo, setDateTo] = useState('');

  const [formData, setFormData] = useState<Partial<ProductionEntry>>({
    date: new Date().toISOString().split('T')[0],
    piecesCount: 0,
    valuePerPiece: 0,
    totalValue: 0,
    notes: ''
  });

  const filteredItems = useMemo(() => {
    return (items || []).filter(item => {
      const isArchived = item.isArchived === true;
      if (archiveMode && !isArchived) return false;
      if (!archiveMode && isArchived) return false;

      const emp = employees.find(e => e.id === item.employeeId);
      const nameMatch = emp?.name.toLowerCase().includes(searchTerm.toLowerCase());
      if (!nameMatch) return false;

      if (dateFrom && item.date < dateFrom) return false;
      if (dateTo && item.date > dateTo) return false;

      return true;
    }).sort((a,b) => b.date.localeCompare(a.date));
  }, [items, archiveMode, searchTerm, dateFrom, dateTo, employees]);

  const totals = useMemo(() => {
    return filteredItems.reduce((acc, curr) => ({
      pieces: acc.pieces + (curr.piecesCount || 0),
      value: acc.value + (curr.totalValue || 0)
    }), { pieces: 0, value: 0 });
  }, [filteredItems]);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!formData.employeeId) return alert('يرجى اختيار موظف');
    const total = (formData.piecesCount || 0) * (formData.valuePerPiece || 0);
    onSave({ 
      ...formData, 
      id: formData.id || Math.random().toString(36).substr(2, 9),
      totalValue: total
    } as ProductionEntry);
    setShowModal(false);
    setFormData({ date: new Date().toISOString().split('T')[0], piecesCount: 0, valuePerPiece: 0, totalValue: 0, notes: '' });
  };

  const handleArchive = (item: ProductionEntry) => {
    if (confirm('هل تريد نقل سجل الإنتاج هذا إلى الأرشيف التاريخي؟')) {
       onSave({ ...item, isArchived: true });
    }
  };

  const handleExportExcel = () => {
    const dataToExport = filteredItems.map(item => {
      const emp = employees.find(e => e.id === item.employeeId);
      return {
        'التاريخ': item.date,
        'اسم الموظف': emp?.name || 'غير معروف',
        'الكمية (قطع)': item.piecesCount,
        'سعر القطعة': item.valuePerPiece,
        'الإجمالي المستحق': item.totalValue,
        'ملاحظات': item.notes || '-'
      };
    });
    exportToExcel(dataToExport, "Production_Report");
  };

  return (
    <div className="space-y-6">
      <div className="flex flex-col md:flex-row justify-between items-center gap-4 bg-white dark:bg-slate-900 p-8 rounded-[2.5rem] shadow-xl border dark:border-slate-800 no-print text-right">
        <div className="flex items-center gap-4">
           <div className={`p-4 rounded-2xl ${archiveMode ? 'bg-amber-100 text-amber-600' : 'bg-indigo-100 text-indigo-600'}`}>
              {archiveMode ? <History size={28}/> : <Zap size={28}/>}
           </div>
           <div className="text-right">
              <h2 className="text-2xl font-black text-indigo-700">إنتاجية الموظفين {archiveMode ? '(الأرشيف)' : ''}</h2>
              <p className="text-xs font-bold text-slate-500">متابعة دقيقة لكميات الإنتاج وقيم الاستحقاق</p>
           </div>
        </div>
        <div className="flex gap-2">
          {!archiveMode && (
            <button onClick={() => { setFormData({ date: new Date().toISOString().split('T')[0], piecesCount: 0, valuePerPiece: 0, totalValue: 0, notes: '' }); setShowModal(true); }} className="bg-indigo-600 text-white px-6 py-3 rounded-2xl font-black flex items-center gap-2 shadow-lg hover:bg-indigo-700 transition"><Plus size={20} /> تسجيل إنتاج جديد</button>
          )}
          <button onClick={onToggleArchive} className={`px-6 py-3 rounded-2xl font-black flex items-center gap-2 shadow-lg transition-all ${archiveMode ? 'bg-indigo-600 text-white' : 'bg-slate-100 text-slate-700'}`}>
             {archiveMode ? <Calendar size={20}/> : <History size={20}/>} {archiveMode ? 'العودة للتسجيل' : 'عرض سجل الأرشيف'}
          </button>
          <button onClick={() => window.print()} className="bg-slate-900 text-white px-6 py-3 rounded-2xl font-black flex items-center gap-2 shadow-lg"><Printer size={20} /> طباعة القائمة</button>
        </div>
      </div>

      <div className="bg-white dark:bg-slate-900 p-6 rounded-[2rem] shadow-lg border dark:border-slate-800 grid grid-cols-1 md:grid-cols-4 gap-4 no-print text-right">
         <div className="relative">
            <Search className="absolute right-3 top-3.5 text-slate-400" size={18}/>
            <input type="text" placeholder="بحث باسم الموظف..." className="w-full pr-10 p-3 bg-slate-50 dark:bg-slate-800 border rounded-xl font-bold" value={searchTerm} onChange={e => setSearchTerm(e.target.value)} />
         </div>
         <div className="relative">
            <Calendar className="absolute right-3 top-3.5 text-slate-400" size={18}/>
            <input type="date" className="w-full pr-10 p-3 bg-slate-50 dark:bg-slate-800 border rounded-xl font-bold text-center" value={dateFrom} onChange={e => setDateFrom(e.target.value)} />
         </div>
         <div className="relative">
            <Calendar className="absolute right-3 top-3.5 text-slate-400" size={18}/>
            <input type="date" className="w-full pr-10 p-3 bg-slate-50 dark:bg-slate-800 border rounded-xl font-bold text-center" value={dateTo} onChange={e => setDateTo(e.target.value)} />
         </div>
         <button onClick={handleExportExcel} className="bg-emerald-600 text-white font-black rounded-xl flex items-center justify-center gap-2 hover:bg-emerald-700 transition">
            <FileDown size={18}/> تصدير البيانات
         </button>
      </div>

      <div className="bg-white dark:bg-slate-900 rounded-[2.5rem] shadow-2xl border dark:border-slate-800 overflow-hidden relative">
        <div className="hidden print:flex justify-between items-start border-b-4 border-indigo-900 pb-6 mb-8 w-full text-indigo-950 p-8">
          <div className="text-right">
            <h1 className="text-3xl font-black leading-none">{settings.name}</h1>
            <p className="text-sm font-black text-indigo-700 mt-2">سجل إنتاجية الموظفين {archiveMode ? '- الأرشيف العام' : ''}</p>
            {(dateFrom || dateTo) && <p className="text-[10px] font-bold text-slate-600">الفترة من {dateFrom || '...'} إلى {dateTo || '...'}</p>}
          </div>
          <div className="flex flex-col items-center">
            {settings.logo && <img src={settings.logo} className="h-16 w-auto object-contain mb-2" alt="Logo" />}
          </div>
          <div className="text-left">
            <p className="text-[10px] font-black text-slate-400">تاريخ الطباعة: {new Date().toLocaleDateString('ar-EG')}</p>
            <p className="text-[10px] font-black text-slate-400">ساعة الطباعة: {new Date().toLocaleTimeString('ar-EG')}</p>
          </div>
        </div>

        <div className="overflow-x-auto">
          <table className="w-full text-right text-sm">
            <thead className="bg-slate-50 dark:bg-slate-800 border-b">
              <tr className="text-slate-900 dark:text-slate-100 font-black text-xs uppercase">
                <th className="px-6 py-5">الموظف / التاريخ</th>
                <th className="px-6 py-5 text-center">كمية الإنتاج (قطع)</th>
                <th className="px-6 py-5 text-center">سعر القطعة</th>
                <th className="px-6 py-5 text-center">إجمالي الاستحقاق</th>
                <th className="px-6 py-5">الملاحظات</th>
                <th className="px-6 py-5 no-print text-center">إجراءات</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-slate-100 dark:divide-slate-800">
              {filteredItems.map(item => (
                <tr key={item.id} className="hover:bg-indigo-50/20 transition font-bold">
                  <td className="px-6 py-5">
                     <p className="font-black text-slate-900 dark:text-white">{employees.find(e => e.id === item.employeeId)?.name}</p>
                     <p className="text-[10px] text-slate-400 font-bold">{item.date}</p>
                  </td>
                  <td className="px-6 py-5 text-center font-black">{item.piecesCount} قطعة</td>
                  <td className="px-6 py-5 text-center">{item.valuePerPiece?.toLocaleString()}</td>
                  <td className="px-6 py-5 text-center font-black text-emerald-600 bg-emerald-50/10">{(item.totalValue || 0).toLocaleString()}</td>
                  <td className="px-6 py-5 text-xs text-slate-500 italic max-w-xs truncate">{item.notes || '-'}</td>
                  <td className="px-6 py-5 text-center no-print">
                     <div className="flex justify-center gap-2">
                      <button onClick={() => onPrintIndividual?.(item)} className="p-2 text-indigo-600 rounded-lg hover:bg-indigo-50 transition"><Printer size={18}/></button>
                      {!archiveMode && <button onClick={() => handleArchive(item)} className="p-2 text-amber-600 rounded-lg hover:bg-amber-50 transition"><Archive size={18}/></button>}
                      <button onClick={() => { setFormData(item); setShowModal(true); }} className="p-2 text-slate-400 rounded-lg hover:bg-slate-100 transition"><Edit2 size={16}/></button>
                      <button onClick={() => onDelete(item.id)} className="p-2 text-rose-600 rounded-lg hover:bg-rose-50 transition"><Trash2 size={16}/></button>
                     </div>
                  </td>
                </tr>
              ))}
            </tbody>
            <tfoot className="bg-indigo-950 text-white font-black text-xs uppercase border-t-4 border-indigo-900">
               <tr>
                 <td className="px-6 py-6 text-right">إجمالي القائمة</td>
                 <td className="px-6 py-6 text-center">{totals.pieces.toLocaleString()} قطعة</td>
                 <td className="px-6 py-6 text-center">-</td>
                 <td className="px-6 py-6 text-center text-lg text-emerald-300">{totals.value.toLocaleString()} {settings.currency}</td>
                 <td className="px-6 py-6"></td>
                 <td className="no-print"></td>
               </tr>
            </tfoot>
          </table>
        </div>
      </div>

      {showModal && (
        <div className="fixed inset-0 bg-slate-950/80 backdrop-blur-xl z-[150] flex items-center justify-center p-4">
          <div className="bg-white dark:bg-slate-900 rounded-[3rem] shadow-2xl w-full max-w-2xl border dark:border-slate-800 overflow-hidden">
            <div className="p-8 bg-indigo-600 text-white border-b flex justify-between items-center text-right">
              <h3 className="text-2xl font-black flex items-center gap-2"><Package/> تسجيل بيانات الإنتاج</h3>
              <button onClick={() => setShowModal(false)} className="text-white hover:text-slate-200 transition"><X size={32}/></button>
            </div>
            <form onSubmit={handleSubmit} className="p-8 space-y-6 text-right">
              <div>
                <label className="text-[10px] font-black text-slate-500 uppercase block mb-1">الموظف المنفذ</label>
                <select className="w-full p-4 border rounded-xl font-black dark:bg-slate-800 dark:text-white" value={formData.employeeId || ''} onChange={e => setFormData({...formData, employeeId: e.target.value})} required>
                  <option value="">-- اختر الموظف --</option>
                  {employees.map(e => <option key={e.id} value={e.id}>{e.name}</option>)}
                </select>
              </div>
              <div className="grid grid-cols-2 gap-4">
                <div>
                   <label className="text-[10px] font-black text-slate-500 uppercase block mb-1">تاريخ العمل</label>
                   <input type="date" className="w-full p-4 border rounded-xl font-bold dark:bg-slate-800 dark:text-white" value={formData.date} onChange={e => setFormData({...formData, date: e.target.value})} />
                </div>
                <div>
                   <label className="text-[10px] font-black text-slate-500 uppercase block mb-1">كمية الإنتاج (عدد القطع)</label>
                   <input type="number" placeholder="مثال: 150" className="w-full p-4 border rounded-xl font-bold dark:bg-slate-800 dark:text-white" value={formData.piecesCount || ''} onChange={e => setFormData({...formData, piecesCount: Number(e.target.value)})} />
                </div>
                <div>
                   <label className="text-[10px] font-black text-slate-500 uppercase block mb-1">سعر القطعة الواحدة</label>
                   <input type="number" placeholder="مثال: 500" className="w-full p-4 border rounded-xl font-bold dark:bg-slate-800 dark:text-white" value={formData.valuePerPiece || ''} onChange={e => setFormData({...formData, valuePerPiece: Number(e.target.value)})} />
                </div>
                <div className="flex flex-col justify-center">
                   <p className="text-[10px] font-black text-indigo-400 uppercase mb-1">إجمالي استحقاق الموظف</p>
                   <div className="bg-indigo-50 dark:bg-indigo-900/20 p-4 rounded-xl border-2 border-dashed border-indigo-200 text-center font-black text-indigo-700">
                      { ((formData.piecesCount || 0) * (formData.valuePerPiece || 0)).toLocaleString() }
                   </div>
                </div>
              </div>
              <div>
                <label className="text-[10px] font-black text-slate-500 uppercase block mb-1">ملاحظات العمل</label>
                <textarea className="w-full p-4 border rounded-xl font-bold dark:bg-slate-800 dark:text-white h-24 outline-none focus:border-indigo-600 transition" placeholder="أية ملاحظات إضافية عن نوع القطع أو الجودة..." value={formData.notes} onChange={e => setFormData({...formData, notes: e.target.value})}></textarea>
              </div>
              <button type="submit" className="w-full bg-indigo-600 text-white py-5 rounded-2xl font-black text-lg shadow-xl hover:bg-indigo-700 transition">تأكيد وحفظ السجل</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Production;

import React, { useState, useMemo } from 'react';
import { Employee } from '../types';
import { Plus, Trash2, Edit2, Printer, Archive, History, X, Search, Calendar, FileDown } from 'lucide-react';
import { exportToExcel } from '../utils/export';

interface GenericModuleProps<T> {
  title: string;
  lang: 'ar' | 'en';
  employees: Employee[];
  items: T[];
  onSave: (item: T) => void;
  onDelete: (id: string) => void;
  onArchive?: (item: T) => void;
  onPrintIndividual?: (item: T) => void;
  archiveMode: boolean;
  onToggleArchive: () => void;
  renderForm: (formData: Partial<T>, setFormData: React.Dispatch<React.SetStateAction<Partial<T>>>) => React.ReactNode;
  renderRow: (item: T, employeeName: string) => React.ReactNode;
  renderFooter?: (filteredItems: T[]) => React.ReactNode;
  tableHeaders: string[];
  initialData: Partial<T>;
  companyName: string;
  logo?: string;
}

export function GenericModule<T extends { id: string; employeeId: string; date?: string; startDate?: string; endDate?: string; isArchived?: boolean }>({ 
  title, lang, employees, items, onSave, onDelete, onArchive, onPrintIndividual, archiveMode, onToggleArchive, renderForm, renderRow, renderFooter, tableHeaders, initialData, companyName, logo 
}: GenericModuleProps<T>) {
  const [showModal, setShowModal] = useState(false);
  const [formData, setFormData] = useState<Partial<T>>(initialData);

  const [archiveSearch, setArchiveSearch] = useState('');
  const [dateFrom, setDateFrom] = useState('');
  const [dateTo, setDateTo] = useState('');

  const filteredItems = useMemo(() => {
    let list = (items || []).filter(item => {
      const archived = item.isArchived === true;
      if (archiveMode && !archived) return false;
      if (!archiveMode && archived) return false;

      const emp = employees.find(e => e.id === item.employeeId);
      const matchesSearch = emp?.name.toLowerCase().includes(archiveSearch.toLowerCase());
      if (!matchesSearch) return false;

      const itemDate = item.date || item.startDate;
      if (itemDate) {
        if (dateFrom && itemDate < dateFrom) return false;
        if (dateTo && itemDate > dateTo) return false;
      }

      return true;
    });
    return list.sort((a, b) => ((b.date || b.startDate) || '').localeCompare((a.date || a.startDate) || ''));
  }, [items, archiveMode, archiveSearch, dateFrom, dateTo, employees]);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!formData.employeeId) return alert('يرجى اختيار الموظف أولاً');
    onSave({ ...formData, id: formData.id || Math.random().toString(36).substr(2, 9) } as T);
    setShowModal(false);
    setFormData(initialData);
  };

  const handleExportExcel = () => {
    const dataToExport = filteredItems.map(item => {
      const emp = employees.find(e => e.id === item.employeeId);
      const row: any = {};

      // تعبئة البيانات بناءً على الرؤوس الموجودة في المكون
      tableHeaders.forEach((header, idx) => {
        if (idx === 0) row[header] = emp?.name || 'غير معروف';
        else {
          // جلب القيم برمجياً بناءً على الحقول المشتركة
          if (header.includes('التاريخ') || header.includes('من')) row[header] = item.date || item.startDate;
          else if (header.includes('إلى')) row[header] = item.endDate;
          else {
             // محاولة جلب القيم الأخرى ديناميكياً
             const keys = Object.keys(item);
             const targetKey = keys.find(k => typeof (item as any)[k] === 'number' || typeof (item as any)[k] === 'string');
             if (targetKey) row[header] = (item as any)[targetKey];
          }
        }
      });
      return row;
    });
    exportToExcel(dataToExport, `${title}_Report`);
  };

  return (
    <div className="space-y-6">
      <div className="hidden print:flex justify-between items-start border-b-4 border-indigo-900 pb-6 mb-8 w-full text-indigo-950">
        <div className="text-right">
          <h1 className="text-3xl font-black leading-none">{companyName}</h1>
          <p className="text-sm font-black text-indigo-700 mt-2">تقرير {title} {archiveMode ? '(الأرشيف التاريخي)' : ''}</p>
          {(dateFrom || dateTo) && <p className="text-[10px] font-bold mt-1 text-slate-600 uppercase">الفترة: {dateFrom || '...'} إلى {dateTo || '...'}</p>}
        </div>
        <div className="flex flex-col items-center">
          {logo && <img src={logo} className="h-14 w-auto object-contain mb-2" alt="Logo" />}
        </div>
        <div className="text-left text-[10px] font-black text-slate-400">
          <p>تاريخ الطباعة: {new Date().toLocaleDateString('ar-EG')}</p>
          <p>الوقت: {new Date().toLocaleTimeString('ar-EG')}</p>
          <p>عدد السجلات: {filteredItems.length}</p>
        </div>
      </div>

      <div className="flex flex-col md:flex-row justify-between items-center gap-6 bg-white dark:bg-slate-900 p-8 rounded-[2.5rem] shadow-xl border dark:border-slate-800 no-print text-right">
        <div className="flex items-center gap-4">
           <div className={`p-4 rounded-2xl ${archiveMode ? 'bg-amber-100 text-amber-600' : 'bg-indigo-100 text-indigo-600'}`}>
              {archiveMode ? <History size={28}/> : <Archive size={28}/>}
           </div>
           <h2 className="text-2xl font-black text-indigo-700">{title} {archiveMode ? '(الأرشيف)' : ''}</h2>
        </div>
        <div className="flex gap-2">
          {!archiveMode && <button onClick={() => { setFormData(initialData); setShowModal(true); }} className="bg-indigo-600 text-white px-8 py-3 rounded-2xl font-black flex items-center gap-2 shadow-lg"><Plus size={20} /> إضافة جديد</button>}
          <button onClick={onToggleArchive} className={`px-8 py-3 rounded-2xl font-black transition-all shadow-lg ${archiveMode ? 'bg-indigo-600 text-white' : 'bg-slate-100 text-slate-700'}`}>
             {archiveMode ? 'العودة للمهام' : 'سجل الأرشيف'}
          </button>
          <button onClick={() => window.print()} className="bg-slate-900 text-white px-8 py-3 rounded-2xl font-black shadow-lg"><Printer size={20} /></button>
        </div>
      </div>

      <div className="bg-white dark:bg-slate-900 p-6 rounded-[2rem] shadow-lg border dark:border-slate-800 grid grid-cols-1 md:grid-cols-4 gap-4 no-print text-right animate-in fade-in duration-300">
         <div className="relative">
            <Search className="absolute right-3 top-3.5 text-slate-400" size={18}/>
            <input type="text" placeholder="بحث باسم الموظف..." className="w-full pr-10 p-3 bg-slate-50 dark:bg-slate-800 border rounded-xl font-bold" value={archiveSearch} onChange={e => setArchiveSearch(e.target.value)} />
         </div>
         <div className="relative">
            <Calendar className="absolute right-3 top-3.5 text-slate-400" size={18}/>
            <input type="date" className="w-full pr-10 p-3 bg-slate-50 dark:bg-slate-800 border rounded-xl font-bold text-center" value={dateFrom} onChange={e => setDateFrom(e.target.value)} />
         </div>
         <div className="relative">
            <Calendar className="absolute right-3 top-3.5 text-slate-400" size={18}/>
            <input type="date" className="w-full pr-10 p-3 bg-slate-50 dark:bg-slate-800 border rounded-xl font-bold text-center" value={dateTo} onChange={e => setDateTo(e.target.value)} />
         </div>
         <button onClick={handleExportExcel} className="bg-emerald-600 text-white font-black rounded-xl flex items-center justify-center gap-2 hover:bg-emerald-700 transition">
            <FileDown size={18}/> تصدير Excel
         </button>
      </div>

      <div className="bg-white dark:bg-slate-900 rounded-[2.5rem] shadow-2xl border dark:border-slate-800 overflow-hidden relative">
        <div className="overflow-x-auto">
          <table className="w-full text-right text-sm">
            <thead className="bg-slate-50 dark:bg-slate-800 border-b">
              <tr className="text-slate-900 dark:text-slate-100 font-black text-xs uppercase">
                {tableHeaders.map((h, i) => <th key={i} className="px-6 py-5">{h}</th>)}
                <th className="px-6 py-5 text-center no-print">إجراءات</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-slate-100 dark:divide-slate-800">
              {filteredItems.map(item => (
                <tr key={item.id} className="hover:bg-slate-50 transition font-bold text-xs">
                  {renderRow(item, employees.find(e => e.id === item.employeeId)?.name || 'Unknown')}
                  <td className="px-6 py-5 text-center no-print">
                    <div className="flex justify-center gap-2">
                      {onPrintIndividual && <button onClick={() => onPrintIndividual(item)} title="طباعة مستند" className="p-2 text-indigo-600 hover:bg-indigo-50 rounded-lg transition"><Printer size={16}/></button>}
                      {!archiveMode && onArchive && <button onClick={() => onArchive(item)} title="نقل للأرشيف" className="p-2 text-amber-600 hover:bg-amber-50 rounded-lg transition"><Archive size={16}/></button>}
                      <button onClick={() => { setFormData(item); setShowModal(true); }} title="تعديل" className="p-2 text-slate-400 hover:bg-slate-100 rounded-lg transition"><Edit2 size={16}/></button>
                      <button onClick={() => onDelete(item.id)} title="حذف" className="p-2 text-rose-600 hover:bg-rose-50 rounded-lg transition"><Trash2 size={16}/></button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
            {renderFooter && (
              <tfoot className="bg-indigo-950 text-white font-black text-xs border-t-4 border-indigo-900">
                 {renderFooter(filteredItems)}
              </tfoot>
            )}
          </table>
        </div>
      </div>

      {showModal && (
        <div className="fixed inset-0 bg-slate-950/80 backdrop-blur-md z-[600] flex items-center justify-center p-4 no-print">
          <div className="bg-white dark:bg-slate-900 rounded-[3.5rem] shadow-2xl w-full max-w-2xl border-4 border-white/20 overflow-hidden relative">
            <div className="p-10 bg-indigo-600 text-white flex justify-between items-center text-right relative">
              <h3 className="text-3xl font-black w-full text-center tracking-tighter">سجل جديد - {title}</h3>
              <button onClick={() => setShowModal(false)} className="absolute left-10 top-1/2 -translate-y-1/2 text-white/80 hover:text-white transition-all"><X size={40}/></button>
            </div>

            <form onSubmit={handleSubmit} className="p-12 space-y-10 text-right">
              <div>
                <label className="block text-[10pt] font-black mb-3 text-slate-400 uppercase tracking-widest mr-2">الموظف المعني</label>
                <select className="w-full p-5 bg-white border-2 border-slate-100 rounded-[1.5rem] font-black outline-none focus:border-indigo-600 transition-all text-xl shadow-sm text-center" value={formData.employeeId || ''} onChange={e => setFormData({...formData, employeeId: e.target.value})} required>
                  <option value="">-- اختر الموظف --</option>
                  {employees.map(e => <option key={e.id} value={e.id}>{e.name}</option>)}
                </select>
              </div>
              <div className="pt-2">{renderForm(formData, setFormData)}</div>
              <div className="flex gap-6 pt-10">
                <button type="submit" className="flex-1 bg-indigo-600 text-white py-6 rounded-[2.5rem] font-black text-2xl shadow-xl hover:bg-indigo-700 transition-all">حفظ البيانات</button>
                <button type="button" onClick={() => setShowModal(false)} className="flex-1 bg-slate-100 py-6 rounded-[2.5rem] font-black text-2xl">إلغاء</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

