                      <option value="sick">مرضية</option>
                      <option value="emergency">طارئة</option>
                      <option value="unpaid">بدون راتب (مخصومة)</option>
                      <option value="marriage">زواج</option>
                      <option value="death">وفاة قريب</option>
                   </select>
