        </div>
        <div className="flex gap-2">
          {!archiveMode && <button onClick={() => { setFormData(initialData); setShowModal(true); }} className="bg-indigo-600 text-white px-5 py-2 rounded-xl font-black text-xs shadow-sm flex items-center gap-2"><Plus size={16} /> جديد</button>}
          <button onClick={onToggleArchive} className={`px-5 py-2 rounded-xl font-black text-xs shadow-sm transition-all ${archiveMode ? 'bg-indigo-600 text-white' : 'bg-slate-100 text-slate-700'}`}>
             {archiveMode ? 'العودة' : 'الأرشيف'}
          </button>
        </div>
      </div>

      <div className="bg-white dark:bg-slate-900 p-4 rounded-2xl shadow-sm border dark:border-slate-800 grid grid-cols-1 sm:grid-cols-4 gap-3 no-print text-right">
         <div className="relative">
            <Search className="absolute right-3 top-2.5 text-slate-400" size={14}/>
            <input type="text" placeholder="بحث..." className="w-full pr-8 p-2 bg-slate-50 dark:bg-slate-800 border dark:border-slate-700 rounded-lg text-xs font-bold" value={archiveSearch} onChange={e => setArchiveSearch(e.target.value)} />
         </div>
         <button onClick={handleExportExcel} className="bg-emerald-600 text-white font-black text-xs rounded-lg py-2 flex items-center justify-center gap-2">
            <FileDown size={14}/> تصدير Excel
         </button>
      </div>

      <div className="bg-white dark:bg-slate-900 rounded-2xl shadow-sm border dark:border-slate-800 overflow-hidden">
        <div className="overflow-x-auto">
          <table className="w-full text-right text-[11px] font-bold">
            <thead className="bg-slate-50 dark:bg-slate-800 border-b dark:border-slate-700">
              <tr className="text-slate-900 dark:text-slate-100 font-black uppercase">
                {tableHeaders.map((h, i) => <th key={i} className="px-5 py-3">{h}</th>)}
                <th className="px-5 py-3 text-center no-print">إجراءات</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-slate-100 dark:divide-slate-800">
              {filteredItems.map(item => (
                <tr key={item.id} className="hover:bg-slate-50 transition">
                  {renderRow(item, employees.find(e => e.id === item.employeeId)?.name || 'Unknown')}
                  <td className="px-5 py-2 text-center no-print">
                    <div className="flex justify-center gap-1">
                      {onPrintIndividual && <button onClick={() => onPrintIndividual(item)} className="p-1.5 text-indigo-600"><Printer size={14}/></button>}
                      <button onClick={() => { setFormData(item); setShowModal(true); }} className="p-1.5 text-slate-400"><Edit2 size={14}/></button>
                      <button onClick={() => onDelete(item.id)} className="p-1.5 text-rose-600"><Trash2 size={14}/></button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {showModal && (
        <div className="fixed inset-0 bg-slate-950/60 backdrop-blur-sm z-[600] flex items-center justify-center p-4 no-print overflow-y-auto">
          <div className="bg-white dark:bg-slate-900 rounded-3xl shadow-xl w-full max-w-xl border dark:border-slate-800 flex flex-col my-10 max-h-[90vh]">
            <div className="p-5 bg-indigo-600 text-white flex justify-between items-center text-right shrink-0">
              <h3 className="text-lg font-black w-full text-center">سجل جديد - {title}</h3>
              <button onClick={() => setShowModal(false)} className="text-white"><X size={24}/></button>
            </div>
            <form onSubmit={handleSubmit} className="p-6 space-y-5 text-right overflow-y-auto flex-1">
              <div>
                <label className="block text-[10px] font-black mb-1 text-slate-400 uppercase tracking-widest">الموظف</label>
                <select className="w-full p-2.5 bg-white dark:bg-slate-800 border rounded-xl font-black text-xs outline-none focus:border-indigo-600" value={formData.employeeId || ''} onChange={e => setFormData({...formData, employeeId: e.target.value})} required>
                  <option value="">-- اختر الموظف --</option>
                  {employees.map(e => <option key={e.id} value={e.id}>{e.name}</option>)}
                </select>
              </div>
              <div>{renderForm(formData, setFormData)}</div>
              <div className="flex gap-3 pt-4 shrink-0">
                <button type="submit" className="flex-1 bg-indigo-600 text-white py-3 rounded-xl font-black text-sm">حفظ</button>
                <button type="button" onClick={() => setShowModal(false)} className="flex-1 bg-slate-100 dark:bg-slate-800 py-3 rounded-xl font-black text-sm text-slate-500">إلغاء</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

import React, { useState, useMemo } from 'react';
import { Employee, AttendanceRecord, CompanySettings } from '../types';
import { Clock, Trash2, Edit2, FileDown, Search, Calendar, Printer, CheckCircle, AlertCircle, Archive, X } from 'lucide-react';
import { calculateTimeDiffMinutes } from '../utils/calculations';
import { exportToExcel } from '../utils/export';

interface Props {
  employees: Employee[];
  records: AttendanceRecord[];
  settings: CompanySettings;
  onSaveRecord: (record: AttendanceRecord) => void;
  onDeleteRecord: (id: string) => void;
  lang: 'ar' | 'en';
  onPrint: () => void;
}

const Attendance: React.FC<Props> = ({ employees, records, settings, onSaveRecord, onDeleteRecord, lang, onPrint }) => {
  const [editingId, setEditingId] = useState<string | null>(null);
  const [selectedEmp, setSelectedEmp] = useState('');
  const [checkIn, setCheckIn] = useState(settings.officialCheckIn);
  const [checkOut, setCheckOut] = useState(settings.officialCheckOut);
  const [date, setDate] = useState(new Date().toISOString().split('T')[0]);

  const [showArchive, setShowArchive] = useState(false);
  const [archiveSearch, setArchiveSearch] = useState('');
  const [archiveEmpId, setArchiveEmpId] = useState('');
  const [dateFrom, setDateFrom] = useState(new Date(new Date().getFullYear(), new Date().getMonth(), 1).toISOString().split('T')[0]);
  const [dateTo, setDateTo] = useState(new Date().toISOString().split('T')[0]);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!selectedEmp) return;
    const emp = employees.find(e => e.id === selectedEmp);
    const shiftIn = emp?.customCheckIn || settings.officialCheckIn;
    const shiftOut = emp?.customCheckOut || settings.officialCheckOut;
    const lateMinutes = Math.max(0, calculateTimeDiffMinutes(checkIn, shiftIn));
    const overtimeMinutes = Math.max(0, calculateTimeDiffMinutes(checkOut, shiftOut));

    onSaveRecord({
      id: editingId || Math.random().toString(36).substr(2, 9),
      employeeId: selectedEmp,
      date, checkIn, checkOut, lateMinutes, overtimeMinutes, status: 'present'
    });

    setEditingId(null); 
    setSelectedEmp('');
  };

  const handleEdit = (r: AttendanceRecord) => {
    setEditingId(r.id);
    setSelectedEmp(r.employeeId);
    setCheckIn(r.checkIn);
    setCheckOut(r.checkOut);
    setDate(r.date);
    if (showArchive) setShowArchive(false);
  };

  const filteredRecords = useMemo(() => {
    return records.filter(r => {
      const emp = employees.find(e => e.id === r.employeeId);
      return (emp?.name.toLowerCase().includes(archiveSearch.toLowerCase()) || !archiveSearch) && r.date === date && !r.isArchived;
    });
  }, [records, date, archiveSearch, employees]);

  const archivedRecords = useMemo(() => {
    return records.filter(r => {
      const emp = employees.find(e => e.id === r.employeeId);
      const nameMatch = emp?.name.toLowerCase().includes(archiveSearch.toLowerCase());
      const empMatch = !archiveEmpId || r.employeeId === archiveEmpId;
      const dateMatch = (!dateFrom || r.date >= dateFrom) && (!dateTo || r.date <= dateTo);
      return nameMatch && empMatch && dateMatch;
    }).sort((a,b) => b.date.localeCompare(a.date));
  }, [records, archiveSearch, archiveEmpId, dateFrom, dateTo, employees]);

  const formatHours = (mins: number) => {
    const h = Math.floor(mins / 60);
    const m = mins % 60;
    return `${h}س ${m}د`;
  };

  const handleExportExcel = () => {
    const dataToExport = archivedRecords.map(r => {
      const emp = employees.find(e => e.id === r.employeeId);
      return {
        'التاريخ': r.date,
        'اسم الموظف': emp?.name || 'غير معروف',
        'وقت الحضور': r.checkIn,
        'وقت الانصراف': r.checkOut,
        'دقائق التأخير': r.lateMinutes,
        'دقائق الإضافي': r.overtimeMinutes,
        'ساعات العمل': formatHours(calculateTimeDiffMinutes(r.checkOut, r.checkIn))
      };
    });
